window.addEventListener("load",function(){function e(){s.classList.contains("show-menu")?s.classList.remove("show-menu"):s.classList.add("show-menu")}for(var n=document.querySelectorAll(".toggle-menu"),s=document.querySelector(".menu-responsivo"),o=0;o<n.length;o++)n[o].addEventListener("click",e);document.addEventListener("keyup",function(n){27==n.keyCode&&s.classList.contains("show-menu")&&e()})});