function menuAction(){wrapper.classList.contains("show-menu")?wrapper.classList.remove("show-menu"):wrapper.classList.add("show-menu")}for(var toogleMenu=document.querySelectorAll(".toggle-menu"),wrapper=document.querySelector(".wrapper"),i=0;i<toogleMenu.length;i++)toogleMenu[i].addEventListener("click",menuAction);document.addEventListener("keyup",function(e){27==e.keyCode&&wrapper.classList.contains("show-menu")&&menuAction()});